<?phpclass Rcl_Payment{        public $id_pay; //идентификатор платежа    public $summ; //сумма платежа    public $type; //тип платежа. 1 - пополнение личного счета, 2 - оплата заказа    public $time; //время платежа    public $user; //идентификатор пользователя    public $pay; //статус платежа        function __construct(){        global $post,$rmag_options;        $this->time = current_time('mysql');        if($post->ID==$rmag_options['page_result_pay']) $this->result();        if($post->ID==$rmag_options['page_success_pay']) $this->success();    }        function result(){	        global $rmag_options;        if($rmag_options['connect_sale']==1) $this->robokassa();	if($rmag_options['connect_sale']==2) $this->interkassa();        if($rmag_options['connect_sale']==3) $this->yandexkassa();                if($this->pay) do_action('payment_rcl',$this->user,$this->summ,$this->id_pay,$this->type);        echo 'OK';         exit;    }        function success(){        global $rmag_options;                if($rmag_options['connect_sale']==1){ //если используется робокасса	                $this->id_pay = $_REQUEST["InvId"];                $this->user = $_REQUEST["shpa"];        }        if($rmag_options['connect_sale']==2){ //если используется Интеркасса	                $this->id_pay = $_REQUEST["ik_pm_no"];	                $this->user = $_REQUEST["ik_x_user_id"];        }        if($this->get_pay()){                wp_redirect(get_permalink($rmag_options['page_successfully_pay'])); exit;		        } else {                wp_die('Запись о платеже в базе данных не найдена');        }    }    function get_pay(){        global $wpdb;         return $wpdb->get_row("SELECT * FROM ".RMAG_PREF ."pay_results WHERE inv_id = '$this->id_pay' AND user = '$this->user'");    }        function insert_pay(){        global $wpdb;                $this->pay = $wpdb->insert( RMAG_PREF .'pay_results',             array(                 'inv_id' => $this->id_pay,                 'user' => $this->user,                 'count' => $this->summ,                 'time_action' => $this->time            )        );                if(!$this->pay) exit;                if($this->type==2){            $order_true = $wpdb->get_row("SELECT * FROM ".RMAG_PREF ."orders_history WHERE order_id = '$this->id_pay' AND status = '1'");            if($order_true) $this->pay_order();        }        if($this->type==1) $this->pay_account();    }        function pay_account(){        global $wpdb;        $oldcount = get_user_money($this->user);                if($oldcount) $newcount = $oldcount + $this->summ;        else $newcount = $this->summ;                update_user_money($newcount,$this->user);                do_action('payment_payservice_rcl',$this->user,$this->summ,'Пополнение личного счета',2);    }        function pay_order(){        global $wpdb;                update_status_order($this->id_pay,2);        do_action('payment_payservice_rcl',$this->user,$this->summ,'Оплата заказа '.$this->id_pay,1);        //Если работает реферальная система и партнеру начисляются проценты с покупок его реферала        if(function_exists('add_referall_incentive_order'))                 add_referall_incentive_order($this->user,$this->summ);        $order_data = get_order($this->id_pay);        $table_order = get_email_table_order_rcl($order_data,$this->id_pay,$this->summ);        $this->mail_success($table_order);    }        function yandexkassa(){                $this->summ = $_REQUEST["orderSumAmount"];        $this->id_pay = $_REQUEST["orderNumber"];               $this->user = $_REQUEST["customerNumber"];        $this->type = $_REQUEST["typePay"];                if($_REQUEST['checkOrder']) $this->check_pay();        $code = $this->check_hash();        if(!$code) $this->insert_pay();        $this->ya_response($code);    }        function check_hash(){        global $rmag_options;                $hash = md5(                $_POST['action']                .';'.$this->summ                .';'.$_POST['orderSumCurrencyPaycash']                .';'.$_POST['orderSumBankPaycash']                .';'.$_POST['shopId']                .';'.$_POST['invoiceId']                .';'.$this->user                .';'.$rmag_options['secret_word']        );                if (strtolower($hash) != strtolower($_POST['md5'])) {                $code = 1;        } else {                           //if (!$this->get_pay()) $code = 200; //Если данного заказа нет                           if ($this->summ != $_POST['orderSumAmount']) {                $code = 100;            } else {                $code = 0;            }                       }                 if($code) $this->mail_error($hash);                return $code;    }        function check_pay(){        $code = $this->check_hash();                $this->ya_response($code);    }        function ya_response($code){        echo '<?xml version="1.0" encoding="UTF-8"?>        <'.$_POST['action'].'Response performedDatetime="'.date('c').'" code="'.$code.'" invoiceId="'.$_POST['invoiceId'].'" shopId="'.$_POST['shopId'].'" />';        die();    }        function robokassa(){        global $rmag_options;                $this->summ = $_REQUEST["OutSum"];        $this->id_pay = $_REQUEST["InvId"];               $this->user = $_REQUEST["shpa"];        $this->type = $_REQUEST["shpb"];        $crc = strtoupper($_REQUEST["SignatureValue"]);        $my_crc = strtoupper(md5                ("$this->summ:"                . "$this->id_pay:"                . "".$rmag_options['tworobopass'].":"                . "Shp_item=".$_REQUEST['Shp_item'].":"                . "shpa=$this->user:"                . "shpb=$this->type"));        if ($my_crc !=$crc){ $this->mail_error($my_crc); die;}        if(!$this->get_pay()) $this->insert_pay();    }        function interkassa(){        global $rmag_options;                $this->summ = $_REQUEST["ik_am"];        $this->id_pay = $_REQUEST["ik_pm_no"];						        $this->user = $_REQUEST["ik_x_user_id"];        $this->type = $_REQUEST["ik_x_type"];        foreach ($_POST as $key => $value) {            if (!preg_match('/ik_/', $key)) continue;            $data[$key] = $value;        }        $ikSign = $data['ik_sign'];        unset($data['ik_sign']);        if ($data['ik_pw_via'] == 'test_interkassa_test_xts') {            $secret_key = $rmag_options['intertestkey'];        } else {            $secret_key = $rmag_options['intersecretkey'];        }        ksort ($data, SORT_STRING);        array_push($data, $secret_key);        $signStr = implode(':', $data);        $sign = base64_encode(md5($signStr, true));				        if ($sign !=$ikSign){ $this->mail_error($sign); die;}        if(!$this->get_pay()) $this->insert_pay();    }        function mail_error($hash=false){	global $rmag_options,$post;		foreach($_REQUEST as $key=>$R){		$textmail .= $key.' - '.$R.'<br>';	}		if($hash){		$textmail .= 'Cформированный хеш - '.$hash.'<br>';		$title = 'Неудачная оплата';	}else{		$title = 'Данные тестового платежа';	}		$textmail .= 'Текущий пост - '.$post->ID.'<br>';	$textmail .= 'RESULT - '.$rmag_options['page_result_pay'].'<br>';	$textmail .= 'SUCCESS - '.$rmag_options['page_success_pay'].'<br>';		$email = $rmag_options['admin_email_magazin_recall'];	if(!$email) $email = get_user_meta( 1, 'user_email', true );						rcl_mail($email, $title, $textmail);			    }    function mail_success($table_order){        global $rmag_options;        //Отправляем письмо об оплате админам        $args = array(                'role' => 'administrator'        );        $users = get_users( $args );        $subject = 'Заказ №'.$this->id_pay.' оплачен!';				        $textmail = '        <p>Пользователь оплатил заказ в магазине "'.get_bloginfo('name').'"</p>        <h3>Информация о покупателе:</h3>        <p><b>Имя</b>: '.get_the_author_meta('display_name',$this->user).'</p>        <p><b>Email</b>: '.get_the_author_meta('user_email',$this->user).'</p>        <h3>Детали заказа:</h3>        '.$table_order.'        <p>Заказ №'.$this->id_pay.' получил статус "Оплачено".</p>        <p>Ссылка для управления заказом в админке:</p>          <p>'.get_bloginfo('wpurl').'/wp-admin/admin.php?page=manage-rmag&order='.$this->id_pay.'</p>';        $admin_email = $rmag_options['admin_email_magazin_recall'];        if($admin_email){            rcl_mail($admin_email, $subject, $textmail);        }else{            foreach((array)$users as $userdata){                    $email = $userdata->user_email;									                    rcl_mail($email, $subject, $textmail);            }        }        //Отправляем письмо об оплате покупателю        $email = get_the_author_meta('user_email',$this->user);				        $textmail = '        <p>Вы оплатили заказ в магазине "'.get_bloginfo('name').'"</p>        <h3>Информация о покупателе:</h3>        <p><b>Имя</b>: '.get_the_author_meta('display_name',$this->user).'</p>        <p><b>Email</b>: '.get_the_author_meta('user_email',$this->user).'</p>        <p>Заказ №'.$this->id_pay.' получил статус "Оплачено".</p>        <p>Ваш заказ оплачен и поступил в обработку. Вы можете следить за сменой его статуса из своего личного кабинета</p>';				        rcl_mail($email, $subject, $textmail);    }}function payments_rcl(){    global $rmag_options;    $reqs = array(0,'InvId','ik_co_id','shopId');    if(!$rmag_options['connect_sale']) return false;    if (isset($_REQUEST[$reqs[$rmag_options['connect_sale']]])){	        $payment = new Rcl_Payment();    }}add_action('wp', 'payments_rcl');